    private void ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonActionPerformed
        
        
        this.labelSaludo.setText(this.agente.getName());
        
        this.agente.setPosicion(this.combo.getItemAt(this.combo.getSelectedIndex()));
        
        if (this.checkIzquierda.isSelected()) {
            agente.setSucioIzquierda(true);
        } else {
            agente.setSucioIzquierda(false);
        }
        
        if (this.checkDerecha.isSelected()) {
            agente.setSucioDerecha(true);
        } else {
            agente.setSucioDerecha(false);
        }
        
        agente.algo();
        
        
    }//GEN-LAST:event_ButtonActionPerformed

    
    
